import React from "react";
import Button from '@material-ui/core/Button';

const styles = {
    btnCss: {
     margin:"4% 0 0 4%",
     borderRadius:"5px"
    },
    imgCss:{
        width:"200px",
        height:'200px'
    }
}


class Home extends React.Component {
    constructor(props) {
      super(props);
      this.state = { image:""}
    }

    componentDidMount() {
        this.imgID = setInterval(() => {
          this.tickImg();
        }, 2000);
      }
      randomImg=()=> {
        clearInterval(this.imgID);
      }
      tickImg=()=> {
          var fileNames = [
              "/01.jpg",
              "/02.jpg",
              "/03.jpg",
              "/04.jpg",
              "/05.jpg",
              "/06.jpg",
              "/07.jpg",
              "/08.jpg",
              "/09.jpg",
              "/10.jpg",
              "/11.jpg",
              "/12.jpg",
            ],
            randomIndex = Math.floor(Math.random() * fileNames.length),
            backgroundImage =  fileNames[randomIndex];
            this.setState({ image: backgroundImage });
      }



    render() {
        return (
            <div>

<Button variant="contained" size="medium" color="primary" style={styles.btnCss} 
onClick={() => this.randomImg} >
          Start
        </Button>


        <div>
        <h4>This is Random IMG:</h4>
        <img src={this.state.image} style={styles.imgCss}></img>
      </div>

            </div>
        )

        }
}
export default Home;
