import React from "react";
import Button from '@material-ui/core/Button';

const styles = {
    btnCss: {
     margin:"4% 0 0 4%",
     borderRadius:"5px"
    },
    imgCss:{
        width:"200px",
        height:'200px'
    },
    container:{
display:"flex",
justifyContent:"center",
alignItems:"center",

    }
}


class Home extends React.Component {
    constructor(props) {
      super(props);
      this.state = { image:"",start:false, canfly:"", answer:["yes","no"],  ans:""}
    }

    componentDidMount() {
        this.imgID = setInterval(() => {
          this.tickImg();
        }, 3000);
        this.answer()
      }
    //   componentWillUnmount() {
    //     clearInterval(this.imgID);
    //   }
    answer=()=>{
       var randomIndex = Math.floor(Math.random() * this.state.answer.length);
        var  ans =  this.state.answer[randomIndex];
          this.setState({ ans });
    }
      tickImg=()=> {
          var fileNames = [
              {img:"/01.jpg",fly:"yes"},
              {img:"/02.jpg",fly:"yes"},
              {img:"/03.jpg",fly:"yes"},
              {img:"/04.jpg",fly:"yes"},
              {img:"/05.jpg",fly:"yes"},
              {img:"/06.jpg",fly:"yes"},
              {img:"/07.jpg",fly:"yes"},
              {img:"/08.jpg",fly:"yes"},
              {img:"/09.jpg",fly:"yes"},
              {img:"/10.jpg",fly:"yes"},
              {img:"/11.jpg",fly:"yes"},
              {img:"/12.jpg",fly:"yes"},
              {img:"/130.jpg",fly:"no"},
              {img:"/131.jpg",fly:"no"},
              {img:"/132.jpg",fly:"no"},
              {img:"/134.jpg",fly:"no"},
              {img:"/121.jpg",fly:"no"},
              {img:"/135.jpg",fly:"no"},
              {img:"/129.jpg",fly:"no"}
            ],
           randomIndex = Math.floor(Math.random() * fileNames.length);
          var  imgObject =  fileNames[randomIndex];
            this.setState({ image: imgObject.img,canfly:imgObject.fly });
      }



    render() {
        return (
            <div>
{!this.state.start ?
<Button variant="contained" size="medium" color="primary" style={styles.btnCss} 
onClick={() => this.setState({start:true})} >
          Start
        </Button>:null}

{this.state.start ?
<>
        <div>
<h4>can fly: {this.state.canfly}</h4>
        <img src={this.state.image} style={styles.imgCss}></img>
      </div>
      <div style={styles.container}>

      <div><img src="/girl.png" style={styles.imgCss}/></div>
      <div><img src="/boy.png" style={styles.imgCss}/></div>

      </div></>
    :null}






            </div>
        )

        }
}
export default Home;
