import React from "react";
import Button from '@material-ui/core/Button';

const styles = {
    btnCss: {
     margin:"4% 0 0 4%",
     borderRadius:"5px"
    },
    imgDiv:{
        width:"200px",
        height:'200px'
    }
}


class Home extends React.Component {
    constructor(props) {
      super(props);
      this.state = {}
    }

    randomImage=()=> {
        var fileNames = [
            "/01.jpg",
            "/02.jpg",
            "/03.jpg",
            "/04.jpg",
            "/05.jpg",
            "/06.jpg",
            "/07.jpg",
            "/08.jpg",
            "/09.jpg",
            "/10.jpg",
            "/11.jpg",
            "/12.jpg",
          ],
          randomIndex = Math.floor(Math.random() * fileNames.length),
          backgroundImage = 'url(' + fileNames[randomIndex] + ')';

        document.getElementById('background').style.backgroundImage = backgroundImage;
      }


    render() {
        return (
            <div>

<Button variant="contained" size="medium" color="primary" style={styles.btnCss} 
onClick={() => this.randomImg} >
          Start
        </Button>


        <div id='background' style={styles.imgDiv}><img src="/01.jpg"/></div>
            </div>
        )

        }
}
export default Home;
